package com.vitamincode.vitamincode_be.controller;import com.vitamincode.vitamincode_be.mapper.ClassesMapper;import com.vitamincode.vitamincode_be.service.ClassesService;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequiredArgsConstructor@RequestMapping("/api/v1/classes")public class ClassesApi {    private final ClassesService classesService;    @GetMapping("/findAllClasses")    public ResponseEntity<?> findAllClasses() {        return ResponseEntity.ok(classesService.findAllClasses());    }}