package com.vitamincode.vitamincode_be.controller;import com.vitamincode.vitamincode_be.dto.request.StudentDtoRequest;import com.vitamincode.vitamincode_be.entity.Students;import com.vitamincode.vitamincode_be.service.StudentsService;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.LinkedHashMap;import java.util.Map;@RestController@RequiredArgsConstructor@RequestMapping("/api/v1/students/")public class StudentsApi {    private final StudentsService studentService;    @GetMapping("findAllStudents")    public ResponseEntity<?> findAllStudents() {        return ResponseEntity.ok(studentService.findAllStudents());    }    @GetMapping("findStudentById/{studentId}")    public ResponseEntity<?> findStudentById(@PathVariable("studentId") Integer studentId) {        return ResponseEntity.ok(studentService.findStudentById(studentId));    }    @PostMapping("insertStudent")    public ResponseEntity<?> insertStudent(@RequestBody StudentDtoRequest studentEntity) {        return ResponseEntity.ok(studentService.insertStudent(studentEntity));    }    @PostMapping("updateStudent")    public ResponseEntity<?> updateStudent(@RequestBody StudentDtoRequest studentEntity) {        return ResponseEntity.ok(studentService.updateStudent(studentEntity));    }    @DeleteMapping("deleteStudent")    public ResponseEntity<?> deleteStudent(@RequestParam("studentId") Integer studentId) {        return ResponseEntity.ok(studentService.deleteStudent(studentId));    }}