package com.vitamincode.vitamincode_be.controller;import com.vitamincode.vitamincode_be.service.StudentsService;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequiredArgsConstructor@RequestMapping("/api/v1/students/")public class StudentsApi {    private final StudentsService studentService;    @GetMapping("findAllStudents")    public ResponseEntity<?> findAllStudents() {        return ResponseEntity.ok(studentService.findAllStudents());    }    @GetMapping("findStudentById/{studentId}")    public ResponseEntity<?> findStudentById(@PathVariable("studentId") Integer studentId) {        return ResponseEntity.ok(studentService.findStudentById(studentId));    }}