package com.vitamincode.vitamincode_be.convert;import com.vitamincode.vitamincode_be.dto.response.ClassDtoResponse;import com.vitamincode.vitamincode_be.entity.Classes;import java.util.List;import java.util.stream.Collectors;public class ClassConvert {    public static ClassDtoResponse classEntityConvertToClassDtoResponse(Classes classEntity) {        return ClassDtoResponse.builder()                .classId(classEntity.getClassId())                .className(classEntity.getClassName())                .classDescription(classEntity.getClassDescription())                .build();    }    public static List<ClassDtoResponse> classEntityConvertListToClassDtoResponse(List<Classes> classEntity) {        return classEntity                .stream()                .map(ClassConvert::classEntityConvertToClassDtoResponse)                .collect(Collectors.toList());    }}