package com.vitamincode.vitamincode_be.convert;import com.vitamincode.vitamincode_be.dto.response.StudentDtoResponse;import com.vitamincode.vitamincode_be.entity.Students;import java.util.List;import java.util.stream.Collectors;public class StudentConvert {    public static StudentDtoResponse studentEntityConvertToStudentDtoResponse(Students studentEntity) {        return StudentDtoResponse.builder()                .studentId(studentEntity.getStudentId())                .studentName(studentEntity.getStudentName())                .studentEmail(studentEntity.getStudentEmail())                .classId(studentEntity.getClassId())                .build();    }    public static List<StudentDtoResponse> studentEntityConvertListToStudentDtoResponse(List<Students> studentEntity) {        return studentEntity                .stream()                .map(StudentConvert::studentEntityConvertToStudentDtoResponse)                .collect(Collectors.toList());    }}