package com.vitamincode.vitamincode_be.service.impl;import com.vitamincode.vitamincode_be.entity.Classes;import com.vitamincode.vitamincode_be.mapper.ClassesMapper;import com.vitamincode.vitamincode_be.service.ClassesService;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;@Service@RequiredArgsConstructorpublic class ClassesServiceImpl implements ClassesService {    private final ClassesMapper classesMapper;    @Override    public List<Classes> findAllClasses() {        return classesMapper.findAllClasses();    }    @Override    public Classes findClassById(Integer classId) {        return classesMapper.findClassById(classId);    }    @Override    public int insertClass(Classes classes) {        return classesMapper.insertClass(classes);    }    @Override    public int updateClass(Classes classes) {        return classesMapper.updateClass(classes);    }    @Override    public int deleteClass(Integer classId) {        return classesMapper.deleteClass(classId);    }}