package com.vitamincode.vitamincode_be.service.impl;import com.vitamincode.vitamincode_be.entity.Classes;import com.vitamincode.vitamincode_be.mapper.ClassesMapper;import com.vitamincode.vitamincode_be.service.ClassesService;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;import java.util.Objects;@Service@RequiredArgsConstructorpublic class ClassesServiceImpl implements ClassesService {    private final ClassesMapper classesMapper;    @Override    public List<Classes> findAllClasses() {        if (!classesMapper.findAllClasses().isEmpty()) {            return classesMapper.findAllClasses();        }        return null;    }    @Override    public List<Classes> findClassByClassName(String className) {        if (!classesMapper.findClassByClassName(className).isEmpty()) {            return classesMapper.findClassByClassName(className);        }        return null;    }    @Override    public Classes findClassById(Integer classId) {        var resultEntity = classesMapper.findClassById(classId);        if (Objects.nonNull(resultEntity)) {            return resultEntity;        }        return null;    }    @Override    public int insertClass(Classes classes) {        return classesMapper.insertClass(classes);    }    @Override    public int updateClass(Classes classes) {        return classesMapper.updateClass(classes);    }    @Override    public int deleteClass(Integer classId) {        return classesMapper.deleteClass(classId);    }}